/// <reference types="react" />
import PropTypes from 'prop-types';
declare const CancelButton: {
    ({ cancelAction, cancelText }: {
        cancelAction: any;
        cancelText: any;
    }): JSX.Element;
    propTypes: {
        cancelAction: PropTypes.Validator<(...args: any[]) => any>;
        cancelText: PropTypes.Requireable<string>;
    };
    defaultProps: {
        cancelText: string;
    };
};
export default CancelButton;
